pub enum CovOption {

    /// The full covariance structure.
    Full,

    /// Adds a regularization constant to the covariance diagonal.
    Regularized(f64),

    /// Only the diagonal covariance structure.
    Diagonal,
}

pub enum ErrorKind {

    /// The parameters used to define the model are not valid.
    InvalidParameters,

    /// The input data to the model is not valid.
    InvalidData,

    /// The action could not be carried out as the model was in an invalid state.
    InvalidState,

    /// The model has not been trained
    UntrainedModel,

    /// Linear algebra related error
    LinearAlgebra,
}

pub enum Regularization<T: Float> {

    /// L1 Regularization
    L1(T),

    /// L2 Regularization
    L2(T),

    /// Elastic Net Regularization (L1 and L2)
    ElasticNet(T, T),

    /// No Regularization
    None,
}
